import React from 'react';
import { useState } from 'react';
const Home = () => {

    const[searchTerm,setSearchTerm]=useState('');
    const [result,setResult]=useState([]);
    const Handlesearch=async(e)=>{
        e.preventDefault();

        if(searchTerm.trim()===''){
            alert("please enter a stock to search");
            return;
          }  
              try {
      const res = await fetch('http://localhost:3001/companies/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ query: searchTerm })  // üëà this is req.body.query on backend
      });
        const data = await res.json();
      setResult(data);
    } catch (error) {
      console.error('‚ùå Search failed:', error);
    }
        
    }
  return (
    <div className="min-h-screen bg-slate-100 flex justify-center items-center dark:bg-gray-900 text-gray-900 dark:text-white">
      <div className="-translate-y-48 text-center">
        <div className="flex justify-center items-center text-5xl text-blue-700 font-bold">
          <img
            className="h-20 w-auto mx-3 border rounded-3xl"
            src="/Finoro logo.png"
            alt="logo"
          />
          FINORO
        </div>
        <h1 className="mt-2 text-xl text-black font-serif">
          Stock analysis and screening tool for investors in India.
        </h1>
        <form onSubmit={Handlesearch} className='mt-4 flex justify-center'>
            <input className='w-4/5 h-12 border-spacing-11 gap-3 mx-1 border-cyan-600 border rounded-3xl  flex text-center'
                type='text'
                placeholder='Search for a stock.....'
                value={searchTerm}
                onChange={(e)=>{setSearchTerm(e.target.value)}}

            />
            <button type='submit'className='px-4 py-2 bg-blue-600 text-white rounded-3xl hover:bg-blue-700'>
                Search
            </button>
        </form>
            <h1 className='flex'>Or Analyze....</h1>
      </div>
    </div>
  );
};

export default Home;
