import React, { useEffect, useState } from 'react';

const News = () => {
  const [news, setNews] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const res = await fetch('http://localhost:3001/news');
        const xmlText = await res.text();

        const parser = new DOMParser();
        const xml = parser.parseFromString(xmlText, 'text/xml');
        const items = xml.querySelectorAll('item');

        const parsedNews = Array.from(items).map((item) => {
          const title = item.querySelector('title')?.textContent;
          const link = item.querySelector('link')?.textContent;
          const pubDate = item.querySelector('pubDate')?.textContent;
          const descriptionRaw = item.querySelector('description')?.textContent || '';
          const description = descriptionRaw.replace(/(<([^>]+)>)/gi, '').slice(0, 200) + '...';

          // Try to find an image in <media:thumbnail> or <media:content> or <enclosure>
          let imageUrl = item.querySelector('media\\:thumbnail')?.getAttribute('url') ||
                         item.querySelector('media\\:content')?.getAttribute('url') ||
                         item.querySelector('enclosure')?.getAttribute('url');

          // Fallback: Try to extract image from HTML in description
          if (!imageUrl) {
            const imgMatch = descriptionRaw.match(/<img[^>]+src="([^">]+)"/);
            if (imgMatch) {
              imageUrl = imgMatch[1];
            }
          }

          return { title, link, pubDate, description, imageUrl };
        });

        setNews(parsedNews);
      } catch (err) {
        console.error(err);
        setError('Failed to load news');
      }
    };

    fetchNews();
  }, []);

  if (error)
    return (
      <div className="p-4 text-red-600 text-center text-lg font-semibold">
        {error}
      </div>
    );

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6 text-center text-blue-800">
        ðŸ“ˆ Market News
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {news.map((item, idx) => (
          <div
            key={idx}
            className="bg-white shadow-md rounded-2xl overflow-hidden hover:shadow-lg transition-shadow duration-300 border border-gray-200"
          >
            {item.imageUrl && (
              <img
                src={item.imageUrl}
                alt="news"
                className="w-full h-48 object-cover"
              />
            )}
            <div className="p-4">
              <a
                href={item.link}
                target="_blank"
                rel="noopener noreferrer"
                className="text-xl font-semibold text-blue-700 hover:underline block mb-2"
              >
                {item.title}
              </a>
              <p className="text-gray-600 text-sm mb-3">{item.description}</p>
              <div className="text-gray-500 text-xs text-right">
                {new Date(item.pubDate).toLocaleString()}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default News;
