import React, { useEffect, useState } from 'react';

const News = () => {
  const [articles, setArticles] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch(
          'https://api.rss2json.com/v1/api.json?rss_url=https://www.moneycontrol.com/rss/MCtopnews.xml'
        );
        const data = await response.json();

        if (data.status !== 'ok') {
          setError('Failed to load news');
          return;
        }

        setArticles(data.items);
      } catch (err) {
        console.error(err);
        setError('Failed to load news');
      }
    };

    fetchNews();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">ðŸ“° Financial News</h2>
      {error && <p className="text-red-600">{error}</p>}

      {articles.map((item, idx) => (
        <div key={idx} className="mb-4 border-b pb-2">
          <a href={item.link} target="_blank" rel="noopener noreferrer" className="text-lg font-semibold text-blue-600">
            {item.title}
          </a>
          <p className="text-sm text-gray-600">{new Date(item.pubDate).toLocaleString()}</p>
        </div>
      ))}
    </div>
  );
};

export default News;
