import React, { useEffect, useState } from 'react';

const NewsFeed = () => {
  const [news, setNews] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const res = await fetch('http://localhost:3001/api/news');
        const xmlText = await res.text();

        const parser = new DOMParser();
        const xml = parser.parseFromString(xmlText, 'text/xml');
        const items = xml.querySelectorAll('item');

        const parsedNews = Array.from(items).map((item) => ({
          title: item.querySelector('title')?.textContent,
          link: item.querySelector('link')?.textContent,
          pubDate: item.querySelector('pubDate')?.textContent,
        }));

        setNews(parsedNews);
      } catch (err) {
        console.error(err);
        setError('Failed to load news');
      }
    };

    fetchNews();
  }, []);

  if (error) return <div className="p-4 text-red-600">{error}</div>;

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">ðŸ“ˆ Market News</h2>
      <ul className="space-y-3">
        {news.map((item, idx) => (
          <li key={idx}>
            <a href={item.link} target="_blank" rel="noopener noreferrer" className="text-blue-700 font-semibold hover:underline">
              {item.title}
            </a>
            <div className="text-sm text-gray-500">{new Date(item.pubDate).toLocaleString()}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default NewsFeed;
