import React, { useState } from 'react';

const Screener = () => {
  const [filters, setFilters] = useState({
    minROCE: '',
    maxROCE: '',
    minPE: '',
    maxPE: '',
    minMarketCap: '',
    maxMarketCap: ''
  });

  const [results, setResults] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSelectedCompany(null); // Clear selection when new filter is applied

    try {
      const res = await fetch('http://localhost:3001/companies/filter', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(filters)
      });

      const data = await res.json();
      setResults(data);
    } catch (error) {
      console.error('Error filtering companies:', error);
    }
  };

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 text-gray-800 dark:text-white px-4 py-10">
      <h1 className="text-3xl font-bold mb-6 text-center">Stock Screener</h1>

      {/* Filter Form */}
      <form
        onSubmit={handleSubmit}
        className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mb-8 max-w-5xl mx-auto"
      >
        <input
          type="number"
          name="minROCE"
          placeholder="Min ROCE"
          value={filters.minROCE}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="number"
          name="maxROCE"
          placeholder="Max ROCE"
          value={filters.maxROCE}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="number"
          name="minPE"
          placeholder="Min P/E Ratio"
          value={filters.minPE}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="number"
          name="maxPE"
          placeholder="Max P/E Ratio"
          value={filters.maxPE}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="number"
          name="minMarketCap"
          placeholder="Min Market Cap (Cr)"
          value={filters.minMarketCap}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="number"
          name="maxMarketCap"
          placeholder="Max Market Cap (Cr)"
          value={filters.maxMarketCap}
          onChange={handleChange}
          className="border p-2 rounded"
        />

        <button
          type="submit"
          className="col-span-1 sm:col-span-2 md:col-span-3 bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Apply Filters
        </button>
      </form>

      {/* Company Cards */}
      {results.length > 0 && (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-6xl mx-auto">
          {results.map((company, index) => (
            <div
              key={index}
              onClick={() => setSelectedCompany(company)}
              className="cursor-pointer border rounded-xl shadow p-4 hover:shadow-lg bg-white dark:bg-gray-800 transition"
            >
              <h2 className="text-xl font-semibold mb-1">{company.name}</h2>
              <p className="text-sm text-gray-500">{company.symbol}</p>
              <div className="mt-3 space-y-1 text-sm">
                <p><strong>ROCE:</strong> {company.ROCE ?? 'N/A'}%</p>
                <p><strong>P/E Ratio:</strong> {company.peRatio ?? 'N/A'}</p>
                <p><strong>Market Cap:</strong> ₹{company.marketCap ?? 'N/A'} Cr</p>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Selected Company Details */}
      {selectedCompany && (
        <div className="max-w-4xl mx-auto mt-10 bg-gray-100 dark:bg-gray-800 p-6 rounded-xl shadow-lg">
          <h2 className="text-2xl font-bold mb-2">{selectedCompany.name}</h2>
          <p className="mb-4 text-gray-500">{selectedCompany.symbol}</p>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 text-sm">
            <p><strong>ROCE:</strong> {selectedCompany.ROCE ?? 'N/A'}%</p>
            <p><strong>P/E Ratio:</strong> {selectedCompany.peRatio ?? 'N/A'}</p>
            <p><strong>Market Cap:</strong> ₹{selectedCompany.marketCap ?? 'N/A'} Cr</p>
            <p><strong>EPS:</strong> {selectedCompany.eps ?? 'N/A'}</p>
            <p><strong>Dividend Yield:</strong> {selectedCompany.dividendYield ?? 'N/A'}%</p>
            <p><strong>ROE:</strong> {selectedCompany.ROE ?? 'N/A'}%</p>
            <p><strong>Price to Book:</strong> {selectedCompany.priceToBook ?? 'N/A'}</p>
            <p><strong>Debt to Equity:</strong> {selectedCompany.debtToEquity ?? 'N/A'}</p>
            <p><strong>Profit Growth:</strong> {selectedCompany.profitGrowth ?? 'N/A'}%</p>
            <p><strong>Sales Growth:</strong> {selectedCompany.salesGrowth ?? 'N/A'}%</p>
          </div>
        </div>
      )}

      {results.length === 0 && (
        <p className="text-center text-gray-600 dark:text-gray-400 mt-6">No companies match the filters.</p>
      )}
    </div>
  );
};

export default Screener;
