import React, { useEffect, useState } from "react";
import axios from "axios";

const Screener = () => {
  const [filters, setFilters] = useState({
    ROCE: "",
    peRatio: "",
    marketCap: "",
  });

  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const fetchFilteredCompanies = async () => {
    try {
      const { ROCE, peRatio, marketCap } = filters;
      const res = await axios.get("http://localhost:5000/api/companies/filter", {
        params: {
          ROCE,
          peRatio,
          marketCap,
        },
      });
      setCompanies(res.data);
      setSelectedCompany(null); // clear old selection
    } catch (err) {
      console.error("Error fetching filtered companies:", err);
    }
  };

  const fetchCompanyDetails = async (symbol) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/companies/${symbol}`);
      setSelectedCompany(res.data);
    } catch (err) {
      console.error("Error fetching company details:", err);
    }
  };

  return (
    <div className="min-h-screen bg-white dark:bg-gray-950 text-gray-900 dark:text-white px-6 py-10">
      <h1 className="text-3xl font-bold mb-8 text-center">Stock Screener</h1>

      {/* Filter Section */}
      <div className="bg-gray-100 dark:bg-gray-800 p-6 rounded-xl shadow-lg">
        <h2 className="text-xl font-semibold mb-4">Custom Filter</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <input
            type="number"
            name="ROCE"
            value={filters.ROCE}
            onChange={handleFilterChange}
            placeholder="Min ROCE %"
            className="p-2 rounded border dark:bg-gray-900"
          />
          <input
            type="number"
            name="peRatio"
            value={filters.peRatio}
            onChange={handleFilterChange}
            placeholder="Max PE Ratio"
            className="p-2 rounded border dark:bg-gray-900"
          />
          <input
            type="number"
            name="marketCap"
            value={filters.marketCap}
            onChange={handleFilterChange}
            placeholder="Min Market Cap (in Cr)"
            className="p-2 rounded border dark:bg-gray-900"
          />
        </div>
        <button
          onClick={fetchFilteredCompanies}
          className="mt-4 px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Apply Filters
        </button>
      </div>

      {/* Company Table */}
      {companies.length > 0 && (
        <div className="mt-8 overflow-x-auto">
          <h3 className="text-xl font-bold mb-3">Filtered Results</h3>
          <table className="min-w-full border divide-y divide-gray-300 dark:divide-gray-700">
            <thead className="bg-gray-200 dark:bg-gray-800">
              <tr>
                <th className="p-2 text-left">Name</th>
                <th className="p-2 text-left">Symbol</th>
                <th className="p-2 text-left">Market Cap</th>
                <th className="p-2 text-left">ROCE</th>
                <th className="p-2 text-left">PE Ratio</th>
              </tr>
            </thead>
            <tbody className="bg-white dark:bg-gray-900 divide-y divide-gray-300 dark:divide-gray-700">
              {companies.map((company) => (
                <tr
                  key={company.id}
                  className="hover:bg-blue-50 dark:hover:bg-blue-900 cursor-pointer"
                  onClick={() => fetchCompanyDetails(company.symbol)}
                >
                  <td className="p-2">{company.name}</td>
                  <td className="p-2">{company.symbol}</td>
                  <td className="p-2">₹{(company.marketCap / 1e9).toFixed(2)}B</td>
                  <td className="p-2">{company.ROCE}</td>
                  <td className="p-2">{company.peRatio}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Popular Company Cards */}
      <div className="mt-12">
        <h2 className="text-xl font-semibold mb-4">Popular Companies</h2>
        <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
          {["AAPL", "MSFT", "GOOG", "NVDA", "AMZN"].map((symbol) => (
            <div
              key={symbol}
              onClick={() => fetchCompanyDetails(symbol)}
              className="bg-gray-100 dark:bg-gray-800 p-4 rounded-xl shadow hover:bg-blue-100 dark:hover:bg-blue-900 text-center font-semibold cursor-pointer"
            >
              {symbol}
            </div>
          ))}
        </div>
      </div>

      {/* Selected Company Details */}
      {selectedCompany && (
        <div className="mt-10 p-6 border rounded-xl shadow bg-white dark:bg-gray-900 text-gray-900 dark:text-white">
          <h3 className="text-2xl font-bold mb-2">
            {selectedCompany.name} ({selectedCompany.symbol})
          </h3>
          <p><strong>Market Cap:</strong> ₹{(selectedCompany.marketCap / 1e9).toFixed(2)}B</p>
          <p><strong>ROCE:</strong> {selectedCompany.ROCE}</p>
          <p><strong>PE Ratio:</strong> {selectedCompany.peRatio}</p>
          <p><strong>Created:</strong> {new Date(selectedCompany.createdAt).toLocaleString()}</p>
          <p><strong>Updated:</strong> {new Date(selectedCompany.updatedAt).toLocaleString()}</p>
        </div>
      )}
    </div>
  );
};

export default Screener;
