import React, { useEffect, useState } from 'react';

const NewsFeed = () => {
  const [news, setNews] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const res = await fetch(
          'https://api.rss2json.com/v1/api.json?rss_url=https://www.moneycontrol.com/rss/MCtopnews.xml'
        );
        const data = await res.json();

        if (data.status !== 'ok') {
          setError('Failed to load news');
          return;
        }

        setNews(data.items);
      } catch (err) {
        console.error(err);
        setError('Failed to fetch news.');
      }
    };

    fetchNews();
  }, []);

  if (error) return <div className="p-4 text-red-600">{error}</div>;

  if (!news.length) return <div className="p-4">Loading news...</div>;

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">ðŸ“° Latest Market News</h2>
      <div className="grid gap-4">
        {news.slice(0, 8).map((item) => (
          <a
            key={item.guid}
            href={item.link}
            target="_blank"
            rel="noopener noreferrer"
            className="p-4 border rounded hover:shadow-md transition"
          >
            <h3 className="text-lg font-semibold">{item.title}</h3>
            <p className="text-sm text-gray-600">{item.pubDate}</p>
          </a>
        ))}
      </div>
    </div>
  );
};

export default News;
